import React from 'react'
import usesStyles from'./style'
import {Card ,CardActions , CardContent, CardMedia,Button,Typography} from '@material-ui/core'
import ThumbUpAltIcon from '@material-ui/icons/ThumbUpAlt'
import DeleteIcon from '@material-ui/icons/Delete'
import MoreHorizIcon from '@material-ui/icons/MoreHoriz'
import moment from 'moment'
import {Consumer} from '../../context'

export default function Posts({post}) {
    const classes=usesStyles()
    const user=JSON.parse(localStorage.getItem('profile'))
    return (
    <Consumer>
        {value=>{
                const {showupdate,likepost,deletepost}=value
                const existinglike=post.likecount.map(res=>{
                    if(res==user?._id){
                        return true
                    }else{
                        return false
                    }
                })
                const ex=post.likecount.filter(res=>res!=String(user?._id)).length
                
            return(
               
                <div className="col-6">
                    
                <Card className={classes.card}>
                <CardMedia className ={classes.media} image={post.selectedfile}
                title={post.title}/>
                <div className={classes.overlay}>
                    <Typography variant='h6'>{post.name}</Typography>
                    <Typography variant='body2'>{moment(post.createdat).fromNow()}</Typography>
                </div>
                <div className={classes.overlay2}>
                {(user?.googleId==post?.creator || user?._id==post?.creator) && (
                        <Button disabled={!user?.name} style={{color:'white'}} size="small" onClick={(e)=>showupdate(e,post._id)}>
                        <MoreHorizIcon fontSize="default"/>
                    </Button>
                    )}
                </div>
                <CardContent>
                <Typography className={classes.title} gutterBottom variant='h8'>{post.tags.map(res=>"#"+res)}</Typography>
                </CardContent>
                <CardContent>
                <Typography className={classes.title} gutterBottom variant='h8'>{post.title}</Typography>
                </CardContent>
                <CardContent>
                <Typography className={classes.title} gutterBottom variant='h9'>{post.message}</Typography>
                </CardContent>
                <CardActions className={classes.cardActions}>
                <Button color='primary' size="small" disabled={!user?.name} onClick={(e)=>{likepost(e,post._id)}}>
                        <ThumbUpAltIcon fontSize="small"/>
                        {existinglike.length!=0 ? ex !=0 ? ("You like And "+post.likecount.length) :"You like " : ex+" Like It"}
                    </Button>
                    {(user?.googleId==post?.creator || user?._id==post?.creator) && (
                    (<Button color='primary' size="small" disabled={!user?.name} onClick={(e)=>deletepost(e,post._id)}>
                        <DeleteIcon fontSize="small"/>
                            Delete
                    </Button>)
                    )}
                    
                </CardActions>
            </Card>
            
              </div>
              
           )}
           }
    </Consumer>
    )
}
