import React from 'react'
import usesStyles from'./style'
import {Card ,CardActions , CardContent, CardMedia,Button,Typography} from '@material-ui/core'
import ThumbUpAltIcon from '@material-ui/icons/ThumbUpAlt'
import DeleteIcon from '@material-ui/icons/Delete'
import {Consumer} from '../../context'

export default function Posts({post}) {
    
    const user=JSON.parse(localStorage.getItem('profile'))
    return (
    <Consumer>
        {value=>{
                const {showupdate,likepost,deletepost}=value
            return(
                <div class="col">
                <div className="card" style="width: 18rem;">
                <img className="card-img-top" src="{post.selectedfile}" alt="Card image cap"/>
                <div className="card-body">
                  <h5 className="card-title">{post.title}</h5>
                  <p className="card-text">{post.message}</p>
                </div>
                <ul className="list-group list-group-flush">
                  <li className="list-group-item">{post.tags.map(res=>"#"+res)}</li>
                </ul>
                <div className="card-body">
                   <Button color='primary' size="small" disabled={!user?.name} onClick={(e)=>{likepost(e,post._id)}}>
                                      <ThumbUpAltIcon fontSize="small"/>
                                      Like
                                      {post.likecount}
                                  </Button>
                                  {(user?.googleId==post?.creator || user?._id==post?.creator) && (
                                  (<Button color='primary' size="small" disabled={!user?.name} onClick={(e)=>deletepost(e,post._id)}>
                                      <DeleteIcon fontSize="small"/>
                                          Delete
                                  </Button>)
                                  )}
                </div>
              </div>
              </div>
           )}
           }
    </Consumer>
    )
}
